package numbers;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=7;
		
		if(i%2==0)
		{
			System.out.println("The given nuber is even");
		}
		else
		{
			System.out.println("The given number is Odd");
		}
	}

}
